mapper.select:
select cp.id,cp.display_id,cp.cid,cp.pid,cp.peid,cp.display_title,cp.color, (select count(*) from judge
cpid=cp.id and cid=cp.cid and pid=cp.pid
and uid NOT IN
#{item}
AND submit_time >= #{startTime}
AND #{selectedTime} >= submit_time
AND #{endTime}>submit_time
AND #{sealTime}>submit_time
AND #{endTime}>submit_time
) as total, (select count(*) from judge
cpid=cp.id and cid=cp.cid and pid =cp.pid and status=0
and uid NOT IN
#{item}
AND submit_time >= #{startTime}
AND #{selectedTime} >= submit_time
AND #{endTime}>submit_time
AND #{sealTime}>submit_time
AND #{endTime}>submit_time
) as ac from contest_problem cp,problem p where cp.cid = #{cid} and cp.pid=p.id and p.auth!=2 order by CAST(cp.display_id AS SIGNED),cp.display_id ASC
