mapper.select:
select m.id as id, m.sender_id as sender_id, m.action as 'action', m.source_id as source_id, m.source_type as source_type, m.source_content as source_content, m.quote_id as quote_id, m.quote_type as quote_type, m.url as url, m.state as state, m.gmt_create as gmt_create, u.username as username, u.avatar as avatar from msg_remind m,user_info u
m.sender_id = u.uuid and m.recipient_id = #{uid}
and (m.action = 'Like_Post' OR m.action = 'Like_Discuss')
and m.action = #{action}
order by m.state asc, m.gmt_create desc

SELECT c.id AS comment_id, u1.username AS username, u2.username AS toUsername, r.content AS content FROM msg_remind m JOIN user_info u1 ON m.sender_id = u1.uuid JOIN user_info u2 ON m.recipient_id = u2.uuid LEFT JOIN comment c ON c.id = m.quote_id LEFT JOIN reply r ON r.from_uid = m.recipient_id AND r.comment_id = m.quote_id
m.action = 'Invent' AND c.cid = m.source_id AND m.recipient_id = #{toUid} AND m.source_id = #{cid}
AND m.sender_id = #{uid}
order by content desc

SELECT c.id AS comment_id, c.from_name AS username, u_r.username AS toUsername, r.content AS content FROM comment c JOIN user_info u ON c.from_uid = u.uuid LEFT JOIN msg_remind m ON m.quote_id = c.id AND m.action = 'Invent' LEFT JOIN user_info u_r ON m.recipient_id = u_r.uuid LEFT JOIN reply r ON r.from_uid = m.recipient_id AND r.to_uid = c.from_uid AND r.comment_id = c.id
AND c.from_uid = #{uid} AND c.cid = #{cid}
AND m.recipient_id = #{toUid}
order by content desc
