mapper.select:
SELECT u.uuid as uid,u.username,u.nickname,u.gender,u.signature,us.school,u.github,u.blog, u.avatar,u.title_name,u.title_color, (SELECT COUNT( pid ) FROM judge WHERE uid = u.uuid) AS total FROM user_info u,user_record ur,user_sign us
u.uuid = ur.uid AND u.uuid = us.uid AND u.status = 0
AND u.uuid = #{uid}
AND u.username = #{username}

SELECT DISTINCT j1.display_pid FROM judge j1
WHERE j1.uid=#{uid}
AND NOT EXISTS (
    SELECT 1 FROM judge j2
    WHERE j2.pid = j1.pid AND j2.username = j1.username AND j2.status = 0
)
AND EXISTS (
    SELECT 1 FROM problem p
    WHERE p.id = j1.pid AND p.auth = 1
);
